// HTML Newsletter Generation Function for n8n
// This function formats the aggregated job data into an HTML newsletter

function generateNewsletter() {
  // Get current date for the newsletter
  const currentDate = new Date().toLocaleDateString('en-US', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  // Start building the HTML newsletter
  let htmlContent = `
  <!DOCTYPE html>
  <html lang="en">
  <head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Job Opportunities Newsletter</title>
    <style>
      body {
        font-family: Arial, sans-serif;
        line-height: 1.6;
        color: #333;
        max-width: 800px;
        margin: 0 auto;
        padding: 20px;
      }
      .header {
        background-color: #0066cc;
        color: white;
        padding: 20px;
        text-align: center;
        border-radius: 5px 5px 0 0;
      }
      .job-listing {
        border: 1px solid #ddd;
        padding: 15px;
        margin-bottom: 20px;
        border-radius: 5px;
        background-color: #f9f9f9;
      }
      .job-title {
        color: #0066cc;
        margin-top: 0;
        margin-bottom: 5px;
        font-size: 18px;
      }
      .job-company {
        font-weight: bold;
        margin-bottom: 5px;
      }
      .job-details {
        margin-bottom: 10px;
        font-size: 14px;
        color: #666;
      }
      .job-description {
        margin-bottom: 15px;
      }
      .job-salary {
        font-weight: bold;
        color: #4CAF50;
      }
      .job-link {
        display: inline-block;
        background-color: #0066cc;
        color: white;
        padding: 8px 15px;
        text-decoration: none;
        border-radius: 3px;
        margin-top: 10px;
      }
      .job-link:hover {
        background-color: #004c99;
      }
      .footer {
        background-color: #f5f5f5;
        border-top: 1px solid #ddd;
        padding: 20px;
        margin-top: 30px;
        border-radius: 0 0 5px 5px;
      }
      .footer-logo {
        text-align: center;
        margin-bottom: 15px;
      }
      .footer-logo a {
        color: #0066cc;
        font-size: 24px;
        font-weight: bold;
        text-decoration: none;
      }
      .footer-links {
        text-align: center;
        margin-bottom: 15px;
      }
      .footer-links a {
        color: #666;
        text-decoration: none;
        margin: 0 15px;
        font-size: 14px;
      }
      .footer-links a:hover {
        text-decoration: underline;
      }
      .unsubscribe {
        text-align: center;
        font-size: 12px;
        color: #999;
      }
      .unsubscribe a {
        color: #999;
        text-decoration: underline;
      }
    </style>
  </head>
  <body>
    <div class="header">
      <h1>Top Job Opportunities</h1>
      <p>${currentDate}</p>
    </div>
    <div class="content">
      <p>Hello ${$('Get Subscribers').first().json.Name}, here are the latest Jobs 4 U:</p>
  `;

  // Loop through each job (0 to 9) to add to the newsletter
  for (let i = 0; i < 10; i++) {
    // Only include jobs that actually exist in the data
    if ($('Aggregate1').first().json.data[i] && $('Aggregate1').first().json.data[i].title) {
      // Get job details from Aggregate1 node
      const title = $('Aggregate1').first().json.data[i].title || 'No Title';
      const company = $('Aggregate1').first().json.data[i].company || 'Company not specified';
      const location = $('Aggregate1').first().json.data[i].location || 'Location not specified';
      const currency = $('Aggregate1').first().json.data[i].currency || '';
      const minAmount = $('Aggregate1').first().json.data[i].min_amount || 'Not specified';
      const maxAmount = $('Aggregate1').first().json.data[i].max_amount || 'Not specified';
      const datePosted = $('Aggregate1').first().json.data[i].date_posted || 'Date not specified';
      const jobUrl = $('Aggregate1').first().json.data[i].job_url || '#';

      // Get job description from Aggregate node
      const description = $('Aggregate').first().json.data[i]?.message?.content || 'No description available';

      // Add this job to the HTML content
      htmlContent += `
        <div class="job-listing">
          <h2 class="job-title">${title}</h2>
          <div class="job-company">${company}</div>
          <div class="job-details">
            <span>${location}</span> • 
            <span>Posted: ${datePosted}</span>
          </div>
          <div class="job-description">
            ${description}
          </div>
          <div class="job-salary">
            Salary: ${currency}${minAmount} - ${currency}${maxAmount}
          </div>
          <a href="${jobUrl}" class="job-link" target="_blank">Apply Now</a>
        </div>
      `;
    }
  }

  // Add the improved footer
  htmlContent += `
      <div class="footer">
        <div class="footer-logo">
          <a href="https://magnificent-tanuki-11356e.netlify.app/">Job 4 U</a>
        </div>
        <div class="footer-links">
          <a href="https://magnificent-tanuki-11356e.netlify.app/terms">Terms of Service</a>
          <a href="https://magnificent-tanuki-11356e.netlify.app/privacy">Privacy Policy</a>
          <a href="https://magnificent-tanuki-11356e.netlify.app/">Contact Us</a>
        </div>
        <div class="unsubscribe">
          <p>Don't want to receive these emails? <a href="https://magnificent-tanuki-11356e.netlify.app/unsubscribe?email=${$('Get Subscribers').first().json['Email Address']}">Unsubscribe</a></p>
        </div>
      </div>
    </div>
  </body>
  </html>
  `;

  // Return the formatted HTML newsletter
  return { htmlNewsletter: htmlContent };
}

// Return the generated newsletter
return generateNewsletter();

