additionalFields.outputSchema:
{
  "type": "object",
  "properties": {
    "AI_Focus": {
      "type": "integer",
      "description": "Points based on AI focus level: Low (5), Medium (10), High (25)"
    },
    "Technical_Level": {
      "type": "integer",
      "description": "Points based on technical level: Basic (5), Intermediate (15), Advanced (25), Expert (35)"
    },
    "Employee_Count": {
      "type": "integer",
      "description": "Points based on employee count: 0-9 (5), 10-150 (25), 150+ (30)"
    },
    "Agency_Status": {
      "type": "integer",
      "description": "Points based on agency status: NOT Automation Agency (0), Automation Agency (20)"
    },
    "Geography": {
      "type": "integer",
      "description": "Points based on geography: NOT US/Europe (0), US/Europe Based (10)"
    },
    "total_score": {
      "type": "integer",
      "description": "Total composite score calculated from all categories"
    },
    "justifications": {
      "type": "object",
      "properties": {
        "AI_Focus": {
          "type": "string",
          "description": "Justification for AI focus score"
        },
        "Technical_Level": {
          "type": "string",
          "description": "Justification for technical level score"
        },
        "Employee_Count": {
          "type": "string",
          "description": "Justification for employee count score"
        },
        "Agency_Status": {
          "type": "string",
          "description": "Justification for agency status score"
        },
        "Geography": {
          "type": "string",
          "description": "Justification for geography score"
        }
      },
      "required": [
        "AI_Focus",
        "Technical_Level",
        "Employee_Count",
        "Agency_Status",
        "Geography"
      ],
      "additionalProperties": false
    }
  },
  "required": [
    "AI_Focus",
    "Technical_Level",
    "Employee_Count",
    "Agency_Status",
    "Geography",
    "total_score",
    "justifications"
  ],
  "additionalProperties": false,
  "$schema": "http://json-schema.org/draft-07/schema#"
}
