parameters.jsCode:
// Get comments from HTTP Request node
const comments = $input.first().json.items
//const comments = response.items || [];

// Get video title from the YouTube node (step 5)
//const videoData = ;
const videoTitle = $('Get Youtube Video Details').first().json.snippet.title;

// Extract comment data
const processedComments = comments.map(item => {
  const comment = item.snippet.topLevelComment.snippet;
  return {
    text: comment.textDisplay,
    author: comment.authorDisplayName,
    likes: comment.likeCount || 0,
    publishedAt: comment.publishedAt,
    replyCount: item.snippet.totalReplyCount || 0
  };
});

// Calculate statistics
const totalComments = processedComments.length;
const totalLikes = processedComments.reduce((sum, c) => sum + c.likes, 0);
const avgLikes = totalComments > 0 ? (totalLikes / totalComments).toFixed(2) : 0;
const totalReplies = processedComments.reduce((sum, c) => sum + c.replyCount, 0);

// Get top comments by likes
const topComments = processedComments
  .sort((a, b) => b.likes - a.likes)
  .slice(0, 5);

// Prepare comment texts for AI analysis
const commentTexts = processedComments
  .slice(0, 50) // Limit to 50 comments for AI analysis
  .map(c => c.text)
  .join('\n---\n');

// Basic sentiment analysis (count positive/negative keywords)
const positiveWords = ['love', 'great', 'awesome', 'amazing', 'excellent', 'good', 'fantastic', 'helpful', 'thanks'];
const negativeWords = ['hate', 'terrible', 'awful', 'bad', 'worst', 'horrible', 'useless', 'waste'];

let positiveCount = 0;
let negativeCount = 0;

processedComments.forEach(comment => {
  const lowerText = comment.text.toLowerCase();
  positiveWords.forEach(word => {
    if (lowerText.includes(word)) positiveCount++;
  });
  negativeWords.forEach(word => {
    if (lowerText.includes(word)) negativeCount++;
  });
});

return [{
  json: {
    videoTitle,
    totalComments,
    avgLikes,
    totalReplies,
    topComments,
    commentTexts,
    processedComments,
    sentimentCounts: {
      positive: positiveCount,
      negative: negativeCount,
      neutral: totalComments - positiveCount - negativeCount
    }
  }
}];

const output = JSON.parse($json.output)
 //return output; 
//return output.overall_sentiment_breakdown.Positive;

let common_question = "";

output.common_questions.map(rec => {
  return common_question += "<li>"+rec.trim().replace(new RegExp(".*,$"), "")+"</li>"
})

let key_feedback_points_and_suggestions = "";

output.key_feedback_points.map(rec => {
  return key_feedback_points_and_suggestions += "<li>"+rec.trim().replace(new RegExp(".*,$"), "")+"</li>"
})


let actionable_insights_for_content_improvement = ""

output.actionable_insights.map(rec => {
  return actionable_insights_for_content_improvement += "<li>"+rec.trim().replace(new RegExp(".*,$"), "")+"</li>"
})


// return {common_question};




//return output; 
// Generate the email HTML
const emailHTML = `
<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    .header { background: #f4f4f4; padding: 20px; border-radius: 5px; }
    .section { margin: 20px 0; }
    .stats { background: #e9ecef; padding: 15px; border-radius: 5px; }
    .analysis { background: #f8f9fa; padding: 20px; border-radius: 5px; }
    h2 { color: #2c3e50; }
    h3 { color: #34495e; }
    h4 { color: #7f8c8d; }
    .footer { margin-top: 30px; padding-top: 20px; border-top: 1px solid #ddd; color: #666; font-size: 0.9em; }
  </style>
</head>
<body>
  <div class="header">
    <h2>YouTube Comments Analysis Report</h2>
    <h3>Video: ${$("Prepare Comments Data").item.json.videoTitle}</h3>
  </div>

  <div class="section stats">
    <h3> Quick Statistics</h3>
    <ul>
      <li><strong>Total Comments Analyzed:</strong> ${$('Prepare Comments Data').item.json.totalComments}</li>
      <li><strong>Average Likes per Comment:</strong> ${$('Prepare Comments Data').item.json.avgLikes}</li>
      <li><strong>Total Replies:</strong> ${$('Prepare Comments Data').item.json.totalReplies}</li>
      <li><strong>Sentiment Summary:</strong> 
        Positive: ${output.overall_sentiment_breakdown.positive}, 
        Negative: ${output.overall_sentiment_breakdown.negative}, 
        Neutral: ${output.overall_sentiment_breakdown.neutral}
      </li>
    </ul>
  </div>

  <div class="section stats">
    <h3>Common Questions</h3>
    <ul>
      ${common_question}
    </ul>
  </div>

    <div class="section stats">
    <h3>Key feedback points and suggestions</h3>
    <ul>
      ${key_feedback_points_and_suggestions}
    </ul>
  </div>


  <div class="section stats">
    <h3>Actionable insights for content improvement</h3>
    <ul>
      ${actionable_insights_for_content_improvement}
    </ul>
  </div>




  <div class="footer">
    <p><em>Report generated on ${new Date().toLocaleString()}</em></p>

