  json: {
    emailHTML,
    subject: `YouTube Analysis: ${$('Prepare Comments Data').item.json.videoTitle} - ${$('Prepare Comments Data').item.json.totalComments} comments analyzed`,
    videoTitle: $('Prepare Comments Data').item.json.videoTitle
  }
};
